  /**
   *
   */


  get draggingFrames() {
    return this.model.parentTimeline.frames.filter(frame => {
      return frame.guiElement.ghost.active;
    });
  }
  /**
   *
   */


  get ghostStart() {
    var x = this.x + this.dragOffset.x;
    x = Math.round(x / this.gridCellWidth);
    x += 1;
    return x;
  }
  /**
   *
   */


  get ghostEnd() {
    var x = this.x + this.dragOffset.x + this.width;
    x = Math.round(x / this.gridCellWidth);
    return x;
  }
